  int n = ni();
  int a = ni();
  int b = ni();
  int[] d = na(n);

  int sd = Arrays.stream(d).sum();
  int tmpA = a + b + sd;
  int tmpB = a - b + sd;
  a = tmpA;
  b = tmpB;
  for (int i = 0; i < n; i ++) {
    d[i] *= 2;
  }

  var dp = new BigInteger[n + 1];
  dp[0] = BigInteger.ONE;
  for (int i = 0; i < n; i ++) {
    dp[i + 1] = dp[i].or(dp[i].shiftLeft(d[i]));
  }

  if (!dp[n].testBit(a) || !dp[n].testBit(b)) {
    System.out.println("No");
    return;
  }

  int[] ret = new int[n];
  for (int i = n - 1; i >= 0; i --) {
    if (!dp[i].testBit(a)) {
      ret[i] |= 1 << 0;
      a -= d[i];
    }
    if (!dp[i].testBit(b)) {
      ret[i] |= 1 << 1;
      b -= d[i];
    }
  }

  char[] s = {'L', 'U', 'D', 'R'};
  out.println("Yes");
  for (int v : ret) {
    out.print(s[v]);
  }
  out.println();
}

