					if(degrees[i] > 1) {

						degrees[i]--;
						map.get(i).remove(next);

						pq.add(new Pair(i, degrees[i]));

						found = true;
						break;

					}else {

						val = 0;
						break;

					}

				}else {

					if(degrees[i] > 1) {

						degrees[i]--;
						found = true;
						break;

					}

				}

			}

			if(found) {

				val = (val * degrees[next]) % MOD;
				degrees[next]--;

			}else {

				val = 0;
				break;

			}

		}

	}

	for(int i = 0; i < n; i++)
		if(degrees[i] > 0)
			remaining = 1;

	if(remaining > 0)
		val = 0;

