    while (System.currentTimeMillis() - start < 1800) {
        int k = Math.min(n, 5);
        int[] v = new int[k];
        for (int i = 0; i < k; i++) {
            v[i] = a[rand.nextInt(n)];
        }
        Arrays.sort(v);
        int gcd = v[1] - v[0];
        for (int i = 2; i < k; i++) {
            if (gcd == 0) {
                gcd = v[i] - v[i - 1];
            } else if (v[i] - v[i - 1] != 0) {
                gcd = gcd(gcd, v[i] - v[i - 1]);
            }
        }
        if (gcd <= 2) {
            continue;
        }
        int va = 0;
        int co = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] % gcd == va) {
                co++;
            } else if (co == 0) {
                va = a[i] % gcd;
                co++;
            } else {
                co--;
            }
        }
        co = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] % gcd == va) {
                co++;
            }
        }
        if (co >= thres) {
            out.println(gcd);
            return;
        }
    }
    out.println(-1);
}

