    long k = K;
    Map<Long,Integer> mp = new HashMap<>();
    for(long i = 2 ; i * i <= K ; i ++ ) {
        if( K % i != 0 ) continue;
        int count = 0 ;
        while(K % i == 0) {
            K /= i;
            count ++ ;
        }
        mp.put(i,count);
    }
    if(K != 1) mp.put(K,1);

    long l = 0;
    long r = k;
    while(r - l > 1) {
        long mid = (r + l)/ 2;
        boolean find = true;
        for(long p : mp.keySet()) {
            int count = 0;
            int pcount = mp.get(p);
            long tmp = mid ;
            while(tmp != 0) {
                tmp /= p;
                count += tmp;
            }
            if(count < pcount) find = false;
        }
        if(find) r = mid ;
        else l = mid;
    }
    out.println(r);
}

