	FastScanner sc = new FastScanner(System.in);
	int N = sc.nextInt();
	int M = sc.nextInt();
	ArrayList<Edge>[] edge = new ArrayList[N];
	Edge[] earr = new Edge[M];
	for (int i = 0; i < N; i++) edge[i] = new ArrayList<>();
	for (int i = 0; i < M; i++) {
		int U = sc.nextInt() - 1;
		int V = sc.nextInt() - 1;

		earr[i] = new Edge(U, V, i);
		edge[U].add(new Edge(V, U, i));
		edge[V].add(earr[i]);
	}
	int K = sc.nextInt();
	int[] x = new int[K];
	HashSet<Integer> S = new HashSet<>();
	for (int i = 0; i < K; i++) {
		x[i] = sc.nextInt() - 1;
		S.add(x[i]);
	}

	UnionFind uf = new UnionFind(N);
	for (int i = 0; i < N; i++) {
		if ( uf.size(i) != 1 ) continue;
		ArrayDeque<Integer> queue = new ArrayDeque<>();
		queue.add(i);
		while ( queue.size() > 0 ) {
			int pos = queue.pollFirst();
			for ( Edge e : edge[i] ) {
				if ( S.contains(e.id) ) continue;
				if ( uf.isSame(pos, e.to) ) continue;
				uf.unite(pos, e.to);
				queue.add(e.to);
			}
		} 
	}

	int[] cnt = new int[N];
	for (int i = 0; i < K; i++) {
		cnt[uf.root(earr[x[i]].from)]++;
		cnt[uf.root(earr[x[i]].to)]++;
	}
	int ocnt = 0;
	for (int i = 0; i < N; i++) {
		if ( cnt[i] % 2 == 1 ) ocnt++;
	}

	printYesNo(ocnt <= 2);
}

