        long[] qPow = new long[zero + 1];
        Arrays.fill(pPow, 1);
        Arrays.fill(qPow, 1);
        for (int j = 0; j < zero; j++) {
            pPow[j + 1] = (pPow[j] * p % MOD + MOD) % MOD;
            qPow[j + 1] = (qPow[j] * (1 - p) % MOD + MOD) % MOD;
        }
        for (int j = rem; j <= zero; j++) {
            long x = binom[zero][j] * pPow[j] % MOD * qPow[zero - j] % MOD;
