static int[] solve() {
    // 次数1の点の横が必ず星の中心
    int[] cnt = new int[N];
    int[] q = new int[N];
    int s = 0, t = 0;
    var G = adjB(N, U, V);
    for (int i = 0; i < N; i++) {
        cnt[i] = G[i].length;
        if( cnt[i] == 1 ) {
            q[t++] = i;
        }
    }

    var ans = new ArrayList<Integer>();
    while( s != t ) {
        int a = q[s++];
        if( cnt[a] != 1 ) continue;

        int center = -1;
        for (int b : G[a]) {
            if( cnt[b] != 0 ) {
                center = b;
                break;
            }
        }

        ans.add( cnt[center] );
        for (int frontier : G[center]) {
            cnt[frontier]--;
            if( cnt[frontier] == 1 ) {
                for (int next : G[frontier]) {
                    if( next != center ) {
                        q[t++] = next;
                        cnt[next]--;
                        cnt[frontier]--;
                    }
                }
            }
        }
    }

    return ans.stream().mapToInt(i -> i).toArray();
}

