	FastScanner sc=new FastScanner();
	PrintWriter pw=new PrintWriter(System.out);

	int N = sc.nextInt();
	int M = sc.nextInt();
	long H = sc.nextLong();
	long[] A = new long[N];
	int[] B = new int[N];
	for (int i = 0; i < N; ++i) {
		A[i] = sc.nextLong();
		B[i] = sc.nextInt() - 1;
	}

	long HP = H;
	class State implements Comparable<State> {
		long point;
		int id;

		public State(long point, int id) {
			this.point = point;
			this.id = id;
		}

		public int compareTo(State o) {
			if (point != o.point) return Long.compare(point, o.point);
			else return Integer.compare(id, o.id);
		};
	}
	TreeSet<State> ok = new TreeSet<>();
	TreeSet<State> ng = new TreeSet<>();
	long[] point = new long[M];
	int[] ans = new int[N];
	for (int i = 0; i < N; ++i) {
		State state = new State(point[B[i]], B[i]);
		if (ok.contains(state)) {
			ok.remove(state);
			state.point += A[i];
			ok.add(state);
		} else {
			if (ng.contains(state)) {
				ng.remove(state);
			}
			state.point += A[i];
			ng.add(state);
			HP -= A[i];
		}
		point[B[i]] += A[i];
		while (!ng.isEmpty() && !ok.isEmpty() && ng.last().point > ok.first().point) {
			State s0 = ok.pollFirst();
			State s1 = ng.pollLast();
			HP += s1.point - s0.point;
			ok.add(s1);
			ng.add(s0);
		}
		while (!ok.isEmpty() && HP - ok.first().point > 0) {
			HP -= ok.first().point;
			ng.add(ok.pollFirst());
		}
		while (HP < 0) {
			State ns = ng.pollLast();
			HP += ns.point;
			ok.add(ns);
		}
		ans[i] = ok.size();
	}
	int[] ans2 = new int[M + 1];
	int p = 0;
	for (int i = 0; i <= M; ++i) {
		while (p < N && ans[p] <= i) ++p;
		ans2[i] = p;
	}
	for (int i = 0; i <= M; ++i) {
		pw.print(ans2[i] + (i == M? "\n" : " "));
	}
	pw.close();
}

