private static String calc(int n, String s) {
	var chars = s.toCharArray();

	var count1 = 0;
	var count2 = 0;
	for (var i = 0; i < n; i++) {
		if (chars[i] == 'B') {
			break;
		}
		count1++;
	}
	for (var i = n-1; i >= 0; i--) {
		if (chars[i] == 'A') {
			break;
		}
		count2++;
	}
	if (count1 + count2 == n && count2 >= count1) {
		return "B";
	}

	return "A";
}

