static boolean pos(int c[],int a,int b,int k) {
	int n=c.length;

	int req=0,can=0;
	for(int i=0;i<n;i++) {
		if(c[i]>k) {
			int d=c[i]-k;
			can+=d/b;
		}
		else {
			int d=k-c[i];
			req+=(d+a-1)/a;
		}
	}

	return can>=req;
}

