			neg += Math.abs(num);
		else
			pos += num;
	}

	if (Math.min(pos, neg) == 0) {
		return null;
	}

	long [] res = new long [prefix.length];
	long lSum = 0;

	int n = prefix.length;
	int idx = 0;

	long [] test = new long [n];


	for (int i = 0; i < n - 1; ++i) {
		if (prefix[i] == 0) 
			continue;

		if ((prefix[n - 1] < 0) != (prefix[i] < 0) ) {
			idx = i;
			break;
		}
	}

	long sum = 0;

	Arrays.fill(test, 1);

	//System.out.println(Arrays.toString(prefix));

	for (int i = 0; i < n - 1; ++i) {
		if (i == idx) 
			continue;

		sum += prefix[i];
	}

	long eSum;
	long absSum, absPrefix;

	long rem;


	eSum = sum + prefix[idx];

	//System.out.println(eSum);

	if ((eSum < 0) != (prefix[idx] < 0) ) {
		absSum = Math.abs(sum);
		absPrefix = Math.abs(prefix[idx]);

		rem = absSum / absPrefix;
		rem += 3;
		test[idx] = rem;
		eSum = sum + test[idx] * prefix[idx];
	}

	sum = eSum;
	//System.out.println(sum);

	test[n - 1] = Math.abs(sum);

	//System.out.println(idx);

