private void solve(final FastIO io, final String[] args) {
	io.setAutoFlush(false);
	io.setAutoOutFlush(false);
	/*
	 * author: 31536000
	 * AtCoder Regular Contest 164 E問題
	 * 考察メモ
	 * まずdは明らかで、ceil(log(N))
	 * 更に、2^d - N個は選ばれても上にできるから、問題名のは残りの2N - 2^d個
	 * これは選ばれると下を呼ぶので、こういうのを最小化したい
	 *
	 * できること
	 * 2. 最下層ではない葉を1個置く
	 * 3. 葉を2連続で置く (片方だけ含む区間の個数だけペナルティ2)
	 *
	 * 左からi番目まで見て、最下層の葉をj個置いた時のペナルティの最小値を考える
	 * 予め、各区間についてここにこの操作をするとペナルティ、を計算しておくと行けるか
	 * 
	 * あー、調査されないなら別に忘れても良いのね
	 * N=4000, Q=[5, 6)だとする
	 * すると、次のことをやっていい
	 * [0, N)
	 * [0, 6) [6, N)
	 * [0, 5) [5, 6)
	 * 残りはさておき、深さは2で抑えられたのか
	 * つまり座圧してから考えて良いのね
	 * あれ、座圧すると葉の話って大丈夫？
	 * 
	 */
	int N = io.nextInt(), Q = io.nextInt();
	TreeMap<Integer, Integer> compress = new TreeMap<>();
	compress.put(0, -1);
	compress.put(N, -1);
	int[] L = new int[Q], R = new int[Q];
	for (int i = 0;i < Q;++ i) {
		L[i] = io.nextInt() - 1;
		R[i] = io.nextInt();
		compress.put(L[i], -1);
		compress.put(R[i], -1);
	}
	int[] penalty;
	int[] penalty2;
	{
		N = compress.size() - 1;
		int val = 0;
		for (int key : compress.keySet().toArray(new Integer[0])) compress.put(key, val++);
		penalty = new int[N];
		for (int i = 0;i < Q;++ i) {
			int l = compress.get(L[i]), r = compress.get(R[i]);
			if (l != 0) penalty[l - 1] += 2;
			penalty[r - 1] += 2;
		}
	}
	int d = 0;
	while(1 << d < N) ++ d;
	int top = (1 << d) - N;
	int[][] dp = new int[N + 2][top + 1]; // dp[i][j]: 左からi番目まで見て、最下層でないものをj個設置したときのペナルティの最小値
	for (int[] i : dp) Arrays.fill(i, exponent10(1, 9));
	dp[0][0] = 0;
	for (int i = 0;i < N;++ i) {
		int[] now = dp[i], next = dp[i + 1], next2 = dp[i + 2];
		for (int j = 0;j <= top;++ j) {
			if (j != top) next[j + 1] = Math.min(next[j + 1], now[j]);// 最下層でない葉を1個
			next2[j] = Math.min(next2[j], now[j] + penalty[i]); // 最下層を2個
		}
	}
	if (d == 0) io.println("0 0");
	else io.println(d + " " + dp[N][top]);
}

