private static boolean solve(long n) {
    // 素因数分解する
    // 試し割りでも sqrt(10^9) は 10^5 には収まるので間に合いそうな予感（怪しいけどTLEしたらエラトステネスさんの技法を採用しよう）
    long m = n;
    long sumOfFactors = 0;
    long i = 2;
    while(m>1) {
        if(m%i > 0) {
            i++;
            if(m<i*i) {
                // 素数
                sumOfFactors += m;
                break;
            }
            continue;
        }
        m = m/i;
        int count = 1;
        while(m%i == 0) {
            m = m/i;
            count++;
        }
        sumOfFactors = i * count;
    }
    return sumOfFactors < n;
}

