static long solve(long K) {
    long left = 0;
    long right = (long)1e12 + 1;
    while (right - left > 1) {
        long mid = left + (right - left) / 2;
        if (smallerNeq(mid) < K) {
            left = mid;
        } else {
            right = mid;
        }
    }
    return left;
}

